<div class="modal_full_size">

    <div class="form_holder">

        <div class="question_form_holder form_11 clearfix">
            <div id="question_type_11_question_preview" class="question question_styles">
                <textarea id="question_type_11_question" name="question_type_11_question" placeholder="Enter your question here" maxlength="100"></textarea>
            </div>
            <div id="question_type_11_image_preview" class="question_image question_image_style pic1">
                <div id="question_type_11_question_image" class="image_holder_5_question">
                    <img class="img_to_upload" src="" />
                    <input type="hidden" name="question_type_11_question_image" class="image_name" id="question_type_11_image_name"/>
                    <button class="btn btn-warning set_image_btn">Set image</button>
                </div>
            </div>

            <div class="answer_holder a1">
                <div id="question_type_11_answer_1_preview" class="answer answer_image_style a1">
                    <div id="question_type_11_image_answer_1" class="image_holder_5 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input type="hidden" name="question_type_11_answer_image[1]" class="image_name" id="question_type_11_image_1_name"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>
                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_11_correct_1" class="question_type_11_correct"
                           name="question_type_11_correct" value="1"/>
                </label>
            </div> <!-- /Answer Holder 1-->

            <div class="answer_holder a2">
                <div id="question_type_11_answer_2_preview" class="answer answer_image_style a2">
                    <div id="question_type_11_image_answer_2" class="image_holder_5 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input type="hidden" name="question_type_11_answer_image[2]" class="image_name" id="question_type_11_image_2_name"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>
                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_11_correct_2" class="question_type_11_correct"
                           name="question_type_11_correct" value="2"/>
                </label>
            </div> <!-- /Answer Holder 2-->

        </div> <!-- /Form 11 -->

    </div> <!-- /Form holder -->

</div>