<div class="modal_full_size">

    <div class="form_holder">
        <div class="question_form_holder form_16 clearfix">
            <div id="question_type_16_question_preview" class="question question_styles">
                <textarea id="question_type_16_question" name="question_type_16_question" placeholder="Enter your question here" maxlength="145"></textarea>
            </div>
            <div id="question_type_16_answer_preview" class="answer answer_styles_preview">
                <div class="controls">
                    <input type="text" id="question_type_16_answer" name="question_type_16_answer" maxlength="100"/>
                </div>
            </div>
            <div id="question_type_16_answer_preview_ok" class="answer answer_ok">OK</div>
            <div id="question_type_16_read_text" class="answer read_text_styles_preview">
                <div class="controls">
                    <textarea type="text" id="question_type_16_read_text" name="question_type_16_read_text" maxlength="469"></textarea>
                </div>
            </div>
        </div>
    </div>

</div>