<div class="modal_full_size clearfix">

    <div class="form_holder">
        <div class="question_form_holder form_3 clearfix">
            <div id="question_type_3_question_preview" class="question question_styles">
                <textarea id="question_type_3_question" name="question_type_3_question" placeholder="Enter your question here" maxlength="100"></textarea>
            </div>
            <div id="question_type_3_image_preview" class="question_image question_image_style pic1">
                <div id="question_type_3_image" class="image_holder">
                    <img class="img_to_upload" src=""/>
                    <input type="hidden" name="question_type_3_image" class="image_name" id="question_type_3_image_name"/>
                    <button class="btn btn-warning set_image_btn">Set image</button>
                </div>
            </div>
            <div id="question_type_3_answer_1_preview" class="answer answer_styles a1">
                <div class="controls">
                    <input type="text" id="question_type_3_answer_1" name="question_type_3_answer[1]" maxlength="35"/>
                </div>
            </div>
            <label>
                <span>Correct?</span>
                <input type="radio" id="question_type_3_correct_1" class="question_type_3_correct"
                       name="question_type_3_correct" value="1"/>
            </label>
            <div id="question_type_3_answer_2_preview" class="answer answer_styles a2">
                <div class="controls">
                    <input type="text" id="question_type_3_answer_2" name="question_type_3_answer[2]" maxlength="35"/>
                </div>
            </div>
            <label>
                <span>Correct?</span>
                <input type="radio" id="question_type_3_correct_2" class="question_type_3_correct"
                       name="question_type_3_correct" value="2"/>
            </label>
            <div id="question_type_3_answer_3_preview" class="answer answer_styles a3">
                <div class="controls">
                    <input type="text" id="question_type_3_answer_3" name="question_type_3_answer[3]" maxlength="35"/>
                </div>
            </div>
            <label>
                <span>Correct?</span>
                <input type="radio" id="question_type_3_correct_3" class="question_type_3_correct"
                       name="question_type_3_correct" value="3"/>
            </label>
            <div id="question_type_3_answer_4_preview" class="answer answer_styles a4">
                <div class="controls">
                    <input type="text" id="question_type_3_answer_4" name="question_type_3_answer[4]" maxlength="35"/>
                </div>
            </div>
            <label>
                <span>Correct?</span>
                <input type="radio" id="question_type_3_correct_4" class="question_type_3_correct"
                       name="question_type_3_correct" value="4"/>
