<div class="modal_full_size">

    <div class="form_holder form_edit_bg">

        <div class="question_form_holder form_11 clearfix">

            <div id="question_type_11_question_preview" class="question question_styles">
                <textarea id="question_type_11_question_edit" name="question_type_11_question" placeholder="Enter your question here" maxlength="100"></textarea>
            </div>

            <div id="question_type_11_image_preview_edit" class="question_image question_image_style pic1">
                <div id="question_type_11_question_image_edit" class="image_holder_11_question">
                    <img class="img_to_upload" src="" />
                    <input class="image_name" type="hidden" name="question_type_11_question_image" id="question_type_11_question_name_edit"/>
                    <button class="btn btn-warning set_image_btn">Set image</button>
                </div>
            </div>

            <div class="answer_holder a1">

                <div id="question_type_11_answer_1_preview_edit" class="answer answer_image_style a1">
                    <div id="question_type_11_image_answer_1_edit" class="image_holder_11 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input class="image_name" type="hidden" name="question_type_11_answer_image[1]" id="question_type_11_image_1_name_edit"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>

                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_11_correct_1_edit" class="question_type_11_correct"
                           name="question_type_11_correct" value="1"/>
                </label>

            </div> <!-- /Answer Holder 1-->

            <div class="answer_holder a2">

                <div id="question_type_11_answer_2_preview_edit" class="answer answer_image_style a2">
                    <div id="question_type_11_image_answer_2_edit" class="image_holder_11 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input class="image_name" type="hidden" name="question_type_11_answer_image[2]" id="question_type_11_image_2_name_edit"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>

                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_11_correct_2_edit" class="question_type_11_correct"
                           name="question_type_11_correct" value="2"/>
                </label>

            </div> <!-- /Answer Holder 2-->

        </div> <!-- /Form 5 -->

    </div> <!-- /Form holder -->

</div>