<div class="modal_full_size">

    <div class="form_holder form_edit_bg">
        <div class="question_form_holder form_4 clearfix">
            <div id="question_type_12_question_preview" class="question question_styles">
                <div class="controls">
                    <textarea id="question_type_12_question_edit" name="question_type_12_question" placeholder="Enter your question here" maxlength="100"></textarea>
                </div>
            </div>
            <div class="answer_holder a1">

                <div id="question_type_12_answer_1_preview_edit" class="answer answer_image_style a1">
                    <div id="question_type_12_image_answer_1_edit" class="image_holder_12 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input class="image_name" type="hidden" name="question_type_12_image[1]" id="question_type_12_image_1_name_edit"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>
                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_12_correct_1_edit" class="question_type_12_correct"
                           name="question_type_12_correct" value="1"/>
                </label>

            </div>

            <div class="answer_holder a2">

                <div id="question_type_12_answer_2_preview_edit" class="answer answer_image_style a2">
                    <div id="question_type_12_image_answer_2_edit" class="image_holder_12 answer_image_holder">
                        <img class="img_to_upload" src="" />
                        <input class="image_name" type="hidden" name="question_type_12_image[2]" id="question_type_12_image_2_name_edit"/>
                        <button class="btn btn-warning set_small_image_btn">Set image</button>
                    </div>
                </div>
                <label>
                    <span>Correct?</span>
                    <input type="radio" id="question_type_12_correct_2_edit" class="question_type_12_correct"
                           name="question_type_12_correct" value="2"/>
                </label>

            </div>

        </div>
    </div>

</div>